import React, { useEffect, useState } from 'react';
import axios from 'axios';

function StatusTable() {
  const [emails, setEmails] = useState([]); // Initialize emails as an empty array
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmailStatus = async () => {
      try {
        const response = await axios.get('/api/email-status'); // Adjust the endpoint as needed
        setEmails(response.data); // Set the fetched data to emails
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchEmailStatus();
  }, []);

  if (loading) return <p>Loading email statuses...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="status-table">
      <h2>Email Status</h2>
      <table>
        <thead>
          <tr>
            <th>Company Name</th>
            <th>Email</th>
            <th>Status</th>
            <th>Delivery Status</th>
            <th>Opened</th>
          </tr>
        </thead>
        <tbody>
          {emails.map((email, index) => (
            <tr key={index}>
              <td>{email.companyName || 'N/A'}</td>
              <td>{email.email || 'N/A'}</td>
              <td>{email.sendStatus || 'N/A'}</td>
              <td>{email.deliveryStatus || 'N/A'}</td>
              <td>{email.opened ? 'Yes' : 'No'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StatusTable;
