import React, { useEffect, useState} from 'react';
import axios from 'axios';

function StatusTable() {
	const [email, setEmails] = useState([]);

	useEffect(() => {
		const fetchEmailStatus = async () => {
			try {
				const response = await axios.get('/api/email-status');
				setEmails(response.data);
			} catch (error) {
				console.error("failed to fetch email status");
			}
		};

		fetchEmailStatus();A
		const interval = setInterval(fetchEmailStatus, 5000);  //Poll every 5 seconds
		return () => clearInterval(interval);
	}, []);

	return (
		<div className="status-table">
      		<h2>Email Status</h2>
      		<table>
        		<thead>
          			<tr>
            			<th>Company Name</th>
            			<th>Email</th>
            			<th>Status</th>
            			<th>Delivery Status</th>
            			<th>Opened</th>
          			</tr>
        		</thead>
        		<tbody>
          			{emails.map((email) => (
            			<tr key={email.id}>
              				<td>{email.companyName}</td>
              				<td>{email.email}</td>
              				<td>{email.sendStatus}</td>
              				<td>{email.deliveryStatus}</td>
              				<td>{email.opened ? "Yes" : "No"}</td>
            			</tr>
          			))}
        		</tbody>
      		</table>
    	</div>
	);
}

export default StatusTable;
