import React, { useEffect, useState } from 'react';
import { Pie } from 'react-chartjs-2';
import axios from 'axios';

function AnalyticsChart() {
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchAnalyticsData = async () => {
            try {
                const response = await axios.get('/api/email-analytics');
                console.log(response.data); // Check the structure of the received data
                setChartData({
                    labels: ['Sent', 'Scheduled', 'Failed', 'Delivered', 'Bounced'],
                    datasets: [
                        {
                            data: [
                                response.data.sent,
                                response.data.scheduled,
                                response.data.failed,
                                response.data.delivered,
                                response.data.bounced,
                            ],
                            backgroundColor: ['#4caf50', '#ff9800', '#f44336', '#2196f3', '#9c27b0'],
                        },
                    ],
                });
            } catch (error) {
                console.error("Failed to fetch analytics data", error);
            } finally {
                setLoading(false);
            }
        };

        fetchAnalyticsData();
    }, []);

    if (loading) return <p>Loading...</p>;
    
    return (
        <div className="analytics-chart">
            <h2>Email Analytics</h2>
            {chartData.datasets && chartData.datasets.length > 0 ? (
                <Pie data={chartData} />
            ) : (
                <p>No data available</p>
            )}
        </div>
    );
}

export default AnalyticsChart;
